import React, { useState } from 'react';
import Board from './Board';

const WIN_CONDITIONS = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8],
  [0, 3, 6],
  [1, 4, 7],
  [2, 5, 8],
  [0, 4, 8],
  [2, 4, 6],
];

const PLAYERS = {
  X: 'X',
  O: 'O',
};

const Game = () => {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [currentPlayer, setCurrentPlayer] = useState(PLAYERS.X);
  const [winner, setWinner] = useState(null);

  const handleSquareClick = (index) => {
    console.log(index);
    if (squares[index] || winner) return;

    const updatedSquares = [...squares];
    updatedSquares[index] = currentPlayer;
    setSquares(updatedSquares);

    console.log("squares", squares)

    const isWinner = checkWinner(updatedSquares);
    if (isWinner) {
      setWinner(isWinner);
      return;
    }

    console.log("curr", currentPlayer === PLAYERS.X ? PLAYERS.O : PLAYERS.X)
    setCurrentPlayer(currentPlayer === PLAYERS.X ? PLAYERS.O : PLAYERS.X);
    console.log(currentPlayer)

    if (currentPlayer === PLAYERS.X) {
      const bestMove = bestMoveAlgo(updatedSquares, PLAYERS.O);
      updatedSquares[bestMove] = PLAYERS.O;
      setSquares(updatedSquares);

      const isWinner = checkWinner(updatedSquares);
      if (isWinner) {
        setWinner(isWinner);
        return;
      }
    }

    setCurrentPlayer(PLAYERS.X);
  };

  const checkWinner = (squares) => {
    for (const condition of WIN_CONDITIONS) {
      const [a, b, c] = condition;
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }

  const bestMoveAlgo = (squares, player) => {
    let bestScore = -Infinity;
    let bestMove = 0;

    for (let i = 0; i < 9; i++) {
        if (!squares[i]) {
            squares[i] = player
            const score = minimax(squares, player === PLAYERS.X ? PLAYERS.O : PLAYERS.X);
            squares[i] = null

            if (score > bestScore) {
                bestScore = score;
                bestMove = i
            }
        }
    }

    return bestMove
  }

  const minimax = (squares, player) => {

    if (checkWinner(squares) === PLAYERS.O) {
      return 1;
    } else if (checkWinner(squares) === PLAYERS.X) {
      return -1;
    } else if (!squares.length) {
      return 0;
    }

    let bestScore = player === PLAYERS.X ? -Infinity : Infinity;
    let bestMove;

    for (let i = 0; i < 9; i++) {
        if (!squares[i]) {
            squares[i] = player;
            const score = minimax(squares, player === PLAYERS.X ? PLAYERS.O : PLAYERS.X);
            //console.log("updatedsquares", squares)
            squares[i] = null;

            if (player === PLAYERS.X) {
                //bestScore = Math.max(bestScore, score);
                if (score > bestScore) {
                    bestScore = score;
                    bestMove = i;
                }
            } else {
                //bestScore = Math.min(bestScore, score);
                if (score < bestScore) {
                    bestScore = score;
                    bestMove = i;
                }
            }
        }
    }
    //console.log("bestMove", bestMove)
    return bestMove;
  }

  return (
    <div className="game">
      <div className="game-board">
        <Board squares={squares} onClick={handleSquareClick} />
      </div>
    </div>
  )
}
export default Game;
